html:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%}
{%- endmacro -%}
<style>
  .print-format table, .print-format tr, 
  .print-format td, .print-format div, .print-format p {
    font-family: Monospace;
    line-height: 200%;
    vertical-align: middle;
  }
  @media screen {
    .print-format {
      width: 4in;
      padding: 0.25in;
      min-height: 8in;
    }
  }
</style>

   <div class="row">

        <div class="col-xs-12">
        <!-- <div class="row " style=" margin-left: 10px;"> -->
         <header><img src="files/image%20(1).jpg"></header>

        <!-- </div> -->
        <hr style="margin-top: 0px; margin-bottom: 5px;">
        <div class="row">
          <div class="col-xs-5">

          <b style="font-size: 15px">Ship To,</b><br>
          <b>Customer Ref: </b>{{ doc.customer_ref or ''}}<br>
          <b>Company Name: </b>{{ frappe.db.get_value("Customer", doc.customer_ref,"customer_ref") or ''}} <br>
          <b>Contact Person :</b> {{ doc.contact_person_name or ""}}<br>
          <b>Mobile No : </b>{{ doc.mobile_no or "" }}<br>
          <b>Email : </b>{{ doc.email or ""}}<br>
          <b>Date :</b> {{ doc.get_formatted('date') }}<br>
          <b>Lorry No :</b> {{ doc.lorry_no or "" }}<br>
          <b>Trip:</b> {{ doc.trip or "" }}<br>

          <b>Address:</b> {{ doc.address_line_1 or ''}}<br>{% if doc.address_line_2 %}{{ doc.address_line_2 }}<br>{% endif -%}{% if doc.address_line_3 %}{{ doc.address_line_3 }}<br>{% endif -%}
          {% if doc.pin_code %}{{ doc.pin_code }}<br>{% endif -%}
          <b>Contact: </b>
          {% if doc.contact_no %}
          {{ doc.contact_no }}<br>{% endif -%}

          </div>
          <div class="col-xs-7 text-right">
            <div class="pull-right" style=" margin-right: 10px;">
            <h4 text-center>Delivery Order</h4><h4 text-center>{{ doc.delivery_note_no }}</h4>
              <img src="http://www.barcodes4.me/barcode/qr/myfilename.png?value= DS: {{ doc.name }},%0A Delivery Order:{{ doc.delivery_note_no or '' }},%0A Customer: {{ doc.customer_ref }} %0A Date: {{ doc.get_formatted('date') }} %0A Trip: {{doc.trip or ''}} %0A Contact Person: {{doc.contact_person_name or ''}} %0A Mobile No: {{doc.mobile_no or ''}}"  style="width: 150px">
          </div>
          </div>
        </div>

      </div>
    </div>
<hr>
<footer><img src="files/image%20(2).jpg"></footer>
